import { Container, MenuItem, NavDropdown, NavItem, Navbar } from "react-bootstrap";

import { Link } from "react-router-dom";
import React from "react";
import { withRouter } from "react-router";

// css

/**
 * Menu class which is used to render the app main top menu
 */
class Menu extends React.Component {
  constructor(props) {
    super(props);
    this.onLogOutClicked = this.onLogOutClicked.bind(this);
  }

  onLogOutClicked() {
    this.props.history.push("/");
    this.props.doLogOut();
  }

  getLoggedOutRender() {
    return (
      <Navbar fluid inverse fixedTop>
        <Navbar.Header>
          <Navbar.Brand>
            <Link to="/">
              <img alt="" src={logo} style={{ width: 100, height: 50 }} />
            </Link>
          </Navbar.Brand>
          <Navbar.Toggle />
        </Navbar.Header>
      </Navbar>
    );
  }

  //changeTheme(theme) {
  changeTheme() {
    /*const link = find(document.getElementsByTagName('link'), (o) => {
      return o.href.indexOf('bootstrap.min.css') !== -1;
    });
    if (link) {
      link.setAttribute(
        'href',
        `https://bootswatch.com/3/${theme}/bootstrap.min.css`
      );3000
    }*/
    alert("Not implemented yet");
  }

  render() {
    /** If there is not sessionId it means that we are not already been authenticated **/
    if (!this.props.user || !this.props.user.token) {
      return this.getLoggedOutRender();
    }

    return (
      <Navbar>
        <Container>
          <Navbar.Brand href="#home">Navbar with text</Navbar.Brand>
          <Navbar.Toggle />
          <Navbar.Collapse className="justify-content-end">
            <Navbar.Text>
              Signed in as: <a href="#login">Mark Otto</a>
            </Navbar.Text>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    );
  }
}

const MenuWithRouter = withRouter(Menu);
export default MenuWithRouter;
